css:
html, body {
  width: 100%;
  height: 100%;
}

.abs {
  position: absolute;
  width: 100px;
  height: 100px;
  top: 0px;
  left: 0px;
}

.fixed {
  position: fixed;
  width: 100px;
  height: 100px;
  top: 0px;
  left: 0px;
}

.red {
  background: red;
}

.green {
  background: green;
}

.blue {
  background: blue;
}

.containing-block {
  padding: 50px;
  border: solid 10px transparent;
}
