css:
.container {
  width: 500px;
  outline: dashed 1px black;
}

.red-box {
  float: left;
  width: 200px;
  height: 100px;
  background: red;
}

.blue-box {
  display: inline-block;
  width: 100px;
  height: 200px;
  vertical-align: bottom;
  background: blue;
}

.green-box {
  float: left;
  width: 400px;
  height: 100px;
  background: green;
}
